provisioning.template:
#!version:1.0.0.1
account.1.enable = 1
account.1.label = {{display_name}}
account.1.display_name = {{display_name}}
account.1.auth_name = {{extension}}
account.1.user_name = {{extension}}
account.1.password = {{password}}
account.1.sip_server.1.address = {{server_host}}
account.1.sip_server.1.port = {{server_port}}
phone_setting.backgrounds = {{wallpaper}}
{{line_keys_loop}}
linekey.{{index}}.type = {{type}}
linekey.{{index}}.value = {{value}}
linekey.{{index}}.label = {{label}}
{{/line_keys_loop}}
{{contacts_loop}}
remote_phonebook.data.1.url = {{contacts_url_example}}
{{/contacts_loop}}
# Enable the phone lock feature
{{if phone_setting.keyboard_lock}}phone_setting.keyboard_lock = {{phone_setting.keyboard_lock}}
{{/if}}
# Set lock type: 0-Lock all keys; 1-Lock all except dial pad
{{if phone_setting.keyboard_lock.type}}phone_setting.keyboard_lock.type = {{phone_setting.keyboard_lock.type}}
{{/if}}
# Define the unlock PIN (up to 15 characters)
{{if phone_setting.keyboard_lock.password}}phone_setting.keyboard_lock.password = {{phone_setting.keyboard_lock.password}}
{{/if}}
# Idle time before auto-locking (in seconds)
{{if phone_setting.keyboard_lock.timeout}}phone_setting.keyboard_lock.timeout = {{phone_setting.keyboard_lock.timeout}}
{{/if}}
# Hide the "Menu" softkey entirely to prevent tampering
{{if features.enhanced_dss_keys.enable}}features.enhanced_dss_keys.enable = {{features.enhanced_dss_keys.enable}}
{{/if}}
{{if softkey.1.enable}}softkey.1.enable = {{softkey.1.enable}}
{{/if}}
{{if softkey.1.label}}softkey.1.label = {{softkey.1.label}}
{{/if}}
# Change default admin password for web/phone interface
{{if static.security.admin_password}}static.security.admin_password = {{static.security.admin_password}}
{{/if}}
# Disable the web interface for total lockdown (requires factory reset to re-enable)
{{if static.network.web_server.enable}}static.network.web_server.enable = {{static.network.web_server.enable}}
{{/if}}
# Disable Zero Touch (prevents kids from accidentally triggering provisioning at boot)
{{if static.zero_touch.enable}}static.zero_touch.enable = {{static.zero_touch.enable}}
{{/if}}
# Set a custom wallpaper (URL must be accessible by the phone)
{{if phone_setting.backgrounds}}phone_setting.backgrounds = {{phone_setting.backgrounds}}
{{/if}}
# Enable and configure a screensaver
{{if screensaver.enable}}screensaver.enable = {{screensaver.enable}}
{{/if}}
{{if screensaver.wait_time}}screensaver.wait_time = {{screensaver.wait_time}}
{{/if}}
{{if screensaver.type}}screensaver.type = {{screensaver.type}}
{{/if}}
{{if screensaver.upload_url}}screensaver.upload_url = {{screensaver.upload_url}}
{{/if}}
# Set the phone's display name (appears on screen)
{{if account.1.display_name}}account.1.display_name = {{account.1.display_name}}
{{/if}}
# Enable Hotline / Hot Dialing for Account 1
{{if account.1.hotline_number}}account.1.hotline_number = {{account.1.hotline_number}}
{{/if}}
{{if account.1.hotline_delay}}account.1.hotline_delay = {{account.1.hotline_delay}}
{{/if}}
# Reduce wait time after last digit is pressed before dialing automatically
{{if phone_setting.inter_digit_timer}}phone_setting.inter_digit_timer = {{phone_setting.inter_digit_timer}}
{{/if}}
