provisioning.template:
#!version:1.0.0.1
#
# Yealink T58G Provisioning Configuration
# Generated by HH Quick Provisioner v2.2.0
#

## Account Configuration
account.1.enable = 1
account.1.label = {{display_name}}
account.1.display_name = {{display_name}}
account.1.auth_name = {{extension}}
account.1.user_name = {{extension}}
account.1.password = {{password}}
account.1.sip_server.1.address = {{server_host}}
account.1.sip_server.1.port = {{server_port}}
account.1.sip_server.1.transport = 0

## Network Settings
static.network.internet_port.type = 0
static.network.internet_port.ip = 
static.network.internet_port.mask = 
static.network.internet_port.gateway = 

## Wallpaper Configuration
{{if wallpaper}}
phone_setting.backgrounds = {{wallpaper}}
{{/if}}

## Line Keys Configuration
{{line_keys_loop}}
linekey.{{index}}.type = {{type}}
linekey.{{index}}.line = 1
linekey.{{index}}.value = {{value}}
linekey.{{index}}.label = {{label}}
linekey.{{index}}.pickup_value = {{value}}
{{/line_keys_loop}}

## Directory/Contacts Configuration
{{contacts_loop}}
remote_phonebook.data.{{index}}.name = {{name}}
remote_phonebook.data.{{index}}.phone_number = {{number}}
{{if photo_url}}
remote_phonebook.data.{{index}}.photo_url = {{photo_url}}
{{/if}}
{{/contacts_loop}}

## Security Settings
{{if static.security.admin_password}}
static.security.admin_password = {{static.security.admin_password}}
{{/if}}

## Phone Lock Settings
{{if phone_setting.keyboard_lock}}
phone_setting.keyboard_lock = {{phone_setting.keyboard_lock}}
{{/if}}
{{if phone_setting.keyboard_lock.type}}
phone_setting.keyboard_lock.type = {{phone_setting.keyboard_lock.type}}
{{/if}}
{{if phone_setting.keyboard_lock.password}}
phone_setting.keyboard_lock.password = {{phone_setting.keyboard_lock.password}}
{{/if}}
{{if phone_setting.keyboard_lock.timeout}}
phone_setting.keyboard_lock.timeout = {{phone_setting.keyboard_lock.timeout}}
{{/if}}

## Enhanced DSS Keys
{{if features.enhanced_dss_keys.enable}}
features.enhanced_dss_keys.enable = {{features.enhanced_dss_keys.enable}}
{{/if}}

## Softkey Configuration
{{if softkey.1.enable}}
softkey.1.enable = {{softkey.1.enable}}
{{/if}}
{{if softkey.1.label}}
softkey.1.label = {{softkey.1.label}}
{{/if}}

## Web Interface Control
{{if static.network.web_server.enable}}
static.network.web_server.enable = {{static.network.web_server.enable}}
{{/if}}

## Zero Touch Provisioning
{{if static.zero_touch.enable}}
static.zero_touch.enable = {{static.zero_touch.enable}}
{{/if}}

## Screensaver Settings
{{if screensaver.enable}}
screensaver.enable = {{screensaver.enable}}
{{/if}}
{{if screensaver.wait_time}}
screensaver.wait_time = {{screensaver.wait_time}}
{{/if}}
{{if screensaver.type}}
screensaver.type = {{screensaver.type}}
{{/if}}

## Display Name
{{if account.1.display_name}}
account.1.display_name = {{account.1.display_name}}
{{/if}}

## Hotline/Auto-Dial Configuration
{{if account.1.hotline_number}}
account.1.hotline_number = {{account.1.hotline_number}}
{{/if}}
{{if account.1.hotline_delay}}
account.1.hotline_delay = {{account.1.hotline_delay}}
{{/if}}

## Dialing Settings
{{if phone_setting.inter_digit_timer}}
phone_setting.inter_digit_timer = {{phone_setting.inter_digit_timer}}
{{/if}}

## Bluetooth Settings
{{if features.bluetooth.enable}}
features.bluetooth.enable = {{features.bluetooth.enable}}
{{/if}}

## WiFi Settings
{{if features.wifi.enable}}
features.wifi.enable = {{features.wifi.enable}}
{{/if}}

## Time Zone
{{if phone_setting.time_zone}}
phone_setting.time_zone = {{phone_setting.time_zone}}
{{/if}}

## Audio Codec Settings
{{if account.1.codec.1.enable}}
account.1.codec.1.enable = {{account.1.codec.1.enable}}
account.1.codec.1.payload_type = PCMU
account.1.codec.1.priority = 1
{{/if}}
{{if account.1.codec.2.enable}}
account.1.codec.2.enable = {{account.1.codec.2.enable}}
account.1.codec.2.payload_type = PCMA
account.1.codec.2.priority = 2
{{/if}}
{{if account.1.codec.3.enable}}
account.1.codec.3.enable = {{account.1.codec.3.enable}}
account.1.codec.3.payload_type = G722
account.1.codec.3.priority = 0
{{/if}}
{{if account.1.codec.4.enable}}
account.1.codec.4.enable = {{account.1.codec.4.enable}}
account.1.codec.4.payload_type = G729
account.1.codec.4.priority = 3
{{/if}}


## Dial Plan (Digitmap) Configuration
{{digitmap_loop}}
phone_setting.digitmap.{{index}} = {{pattern}}|{{timeout}}
{{/digitmap_loop}}
## NTP Time Server
local_time.ntp_server1 = pool.ntp.org
local_time.ntp_server2 = time.nist.gov

## Provisioning Settings
static.auto_provision.server.url = {{options.provisioning_server}}
static.auto_provision.schedule.periodic_minute = 1440

## End of Configuration
